item.description:
This works on any widget:

&lt;code&gt;
function widget_in_content($atts) {
    extract(shortcode_atts(array(
        &#039;id&#039; =&#062; FALSE
    ), $atts));
    
	ob_start();
	dynamic_sidebar($id);
	$sidebar = ob_get_contents();
	ob_end_clean();
	return $sidebar;
    
}
add_shortcode(&#039;widget_in_content&#039;,&#039;widget_in_content&#039;); 
&lt;/code&gt;
